	[1300] = {
		chuxi = "Commander! Commaaander! Let's go set off some firecrackers! Oh, and hang up fai chun decor, too! ...Hm? I'm awfully excited today? Well, yeah, 'cause today is Chúxī! Now c'mon! Let's go have a fun-filled, unforgettable New Year's Eve!",
		name = "TB",
		voice = "cv-1300",
		type = 4,
		expedition = "Commission complete! Remember to put the rewards to good use. Like for instance, buying yummy-yummy food! What? That's too typical? Aww!",
		chime_9 = "",
		home = "Welcome back! Sweet, looks like you've still got energy left in you! Heheh – I'll assume that means you scored a flawless victory! I'm happy for you!",
		mission = "Hey, you've got a new mission. Heheh! I'll leave you in the dust if you don't stop slacking!",
